mapper.insert:
INSERT INTO article (
    cover, content, title, is_top, status, labels, author_id, category_id, abstract_content, create_time, update_time, read_count, created_by, updated_by
) VALUES (
    #{cover}, #{content}, #{title}, #{isTop}, #{status}, #{labels}, #{authorId}, #{categoryId}, #{abstractContent}, NOW(), NOW(), 0, #{createdBy}, #{updatedBy}
)

mapper.update:
UPDATE article
cover = #{cover}, content = #{content}, title = #{title}, is_top = #{isTop}, status = #{status}, labels = #{labels}, category_id = #{categoryId}, abstract_content = #{abstractContent}, updated_by = #{updatedBy}, update_time = NOW()
WHERE id = #{id}

UPDATE article
SET status = 'DELETE', update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT id, cover, title, content, is_top as isTop, status, labels, category_id as categoryId, author_id as authorId, abstract_content as abstractContent, read_count as readCount, create_time as createTime, update_time as updateTime, created_by as createdBy, updated_by as updatedBy FROM article
AND title LIKE concat('%', #{title}, '%')
AND status = #{status}
AND status != 'DELETE'
AND category_id = #{categoryId}
ORDER BY is_top DESC, create_time DESC

mapper.update:
UPDATE article
SET read_count = read_count + 1
WHERE id = #{id}
